import { ILoggerCallback } from './ILoggerCallback';
export declare type LoggerPayload = Object | ILoggerCallback;
export interface ILogger {
    setLoggedClass(loggedClass: Function): ILogger;
    debug(...payloads: LoggerPayload[]): any;
    info(...payloads: LoggerPayload[]): any;
    log(...payloads: LoggerPayload[]): any;
    warn(...payloads: LoggerPayload[]): any;
    error(...payloads: LoggerPayload[]): any;
    isDebugEnabled(): boolean;
    isInfoEnabled(): boolean;
    isLogEnabled(): boolean;
    isWarnEnabled(): boolean;
    isErrorEnabled(): boolean;
}
